    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        if(validateForm()){      
         int n = Integer.parseInt(jTextField1.getText().trim());
        String pass = textField1.getText().trim();
        
        String role = check_login(n, pass);
        if(role!=null)
        {
           Main obj = new Main();
           this.setVisible(false);
           obj.setVisible(true);
           if(role.equalsIgnoreCase("admin"))
           {
             obj.showAdmin();
           }
           else if(role.equalsIgnoreCase("supplier"))
           {
             obj.showSupplier();
           }
           else if (role.equalsIgnoreCase("operator"))
           {
             obj.showOperator();
           }
        }
        else
        {
         javax.swing.JOptionPane.showMessageDialog(null, "Either Username or password is incorrect");
        }
    }//GEN-LAST:event_jButton1ActionPerformed
}
    private String check_login(int id, String pass)
    {
      String ans=null;
      //int i = jComboBox1.getSelectedIndex();
           try
	    {
          	Class.forName("com.mysql.jdbc.Driver").newInstance();
                Connection cn=DriverManager.getConnection("jdbc:mysql://localhost:3306/main","root","dellstudio15"); 
                String sql= "select role from logindata where id=? and password=?";
		PreparedStatement pst=cn.prepareStatement(sql);

			pst.setInt(1,id);
			pst.setString(2,pass);

			ResultSet rs=pst.executeQuery();

			if(rs.next())
			{
				ans=rs.getString(1);
			}
		}
		catch(Exception e)
		{
			javax.swing.JOptionPane.showMessageDialog(null,""+e);
		}
		return ans;
    }

