    private boolean validateForm()
    {
        GenValidation obj = new GenValidation();
      if(jTextField1.getText().isEmpty())
      {
          javax.swing.JOptionPane.showMessageDialog(null, "Enter User ID");
          return false;
      }
              
       else if(obj.checkNumericField(jTextField1.getText().trim())==false)
       {
          javax.swing.JOptionPane.showMessageDialog(null, "User ID can only be numeric");
          return false;
       }
      else if(textField1.getText().isEmpty())
      {
          javax.swing.JOptionPane.showMessageDialog(null, "Enter Password");
          return false;
      }
      else
          return true;
    }
    
